set-variable.value:
@{
            JObject inBody = context.Request.Body.As<JObject>(preserveContent: true); 
            string subId = inBody.GetValue("apiName").ToString();
            return subId;
        }

@{
            JObject inBody = context.Request.Body.As<JObject>(preserveContent: true); 
            string environment = inBody.GetValue("environment").ToString();
            return environment;
        }

@{
                        var id = "{{eapim-platform-api-management-identifier}}";   
                            var key = "{{eapim-platform-api-management-key}}";   
                            DateTime expiry = DateTime.UtcNow.AddDays(10);
                            string encodedToken = "";   
                            using (var encoder = new System.Security.Cryptography.HMACSHA512(Encoding.UTF8.GetBytes(key)))   
                            {   
                                var dataToSign = id + "\n" + expiry.ToString("O");   
                                var hash = encoder.ComputeHash(Encoding.UTF8.GetBytes(dataToSign));   
                                var signature = Convert.ToBase64String(hash);   
                                encodedToken = string.Format("SharedAccessSignature uid={0}&ex={1:o}&sn={2}", id, expiry, signature);   
                            }                   
                            return encodedToken;
                }

@{
        String apiName =  (string) context.Variables["apiName"]; 
        string url = "{{eapim-platform-management-api-baseurl}}/apis/" +apiName + "/tags?&api-version=2019-12-01";
        return url;
        }

@{
        String apiName =  (string) context.Variables["apiName"]; 
        string url = "{{eapim-platform-management-api-baseurl}}/namedValues?$filter=contains(properties/displayName, '" +apiName + "')&api-version=2019-12-01";
        return url;
        }

return-response.set-body:
@{
  		        JObject tagsDataFromAPI = (JObject)((IResponse)context.Variables["tagsDataForAPI"]).Body.As<JObject>();
  		        JObject tagsDataFromNamedValues = (JObject)((IResponse)context.Variables["tagsDataFromNamedValues"]).Body.As<JObject>();
                JArray values1=(JArray)tagsDataFromAPI.SelectToken("value");
                String apiName  = (string) context.Variables["apiName"];  
                JArray values2=(JArray)tagsDataFromNamedValues.SelectToken("value");
                foreach(JObject innerData in values2) 
                {
                    JObject props = (JObject)innerData["properties"];
                    String sName = props["displayName"].ToString(); 
                    if(sName.Contains("tag")) 
                    { 
                        if(sName.IndexOf(apiName) >= 0)
                        {
                            sName = sName.Remove(sName.IndexOf(apiName), apiName.Length+5);
                            props["displayName"] = sName;
                            innerData["properties"] = props;
                        } 
                     } 
                    values1.Add(innerData);
                }
                return tagsDataFromAPI.ToString();
            }
