set-variable.value:
@{
            JObject inBody = context.Request.Body.As<JObject>(preserveContent: true); 
            string userID = inBody.GetValue("userID").ToString();
            return userID;
        }

@{
            String userID = (string) context.Variables["userID"];
            String backendurl = "{{eapim-platform-api-b2c-graph-applications-url}}?$filter=startswith(displayName,'"+"dfedeveloperhub" + "-" + userID + "')";
            return backendurl;
        }

return-response.set-body:
@{
  		        var appsDataToRead = ((IResponse)context.Variables["appsData"]).Body.As<JObject>();
            	JArray appsArray = new JArray();
            	JObject appsDataToReturn = new JObject();
            	JArray items = (JArray)appsDataToRead["value"];

            	foreach (JObject o in items)
            	{
              	  JObject appsDataToWrite = new JObject();
               	  string sAppId = o["id"].ToString();
              	  string sClientID = o["appId"].ToString();
               	  string sAppName = o["displayName"].ToString();
                  string sCreatedOn = o["createdDateTime"].ToString();  
                  string sDescription = o["description"].ToString();  
                  JObject web = (JObject)o["web"];
                  JArray passwordCredentials = (JArray)o["passwordCredentials"];

               	  appsDataToWrite.Add("applicationId", sAppId);
                  appsDataToWrite.Add("clientId", sClientID);
                  appsDataToWrite.Add("applicationName", sAppName);
                  appsDataToWrite.Add("description", sDescription);
                  appsDataToWrite.Add("createdOn", sCreatedOn);
                  appsDataToWrite.Add("web", web);
                  appsDataToWrite.Add("passwordCredentials",passwordCredentials);


                  appsArray.Add(appsDataToWrite);
            	}
	            appsDataToReturn.Add("Applications", appsArray);
                return appsDataToReturn.ToString();
            }
