set-variable.value:
@{
                var id = "{{eapim-platform-api-management-identifier}}";   
                    var key = "{{eapim-platform-api-management-key}}";   
                    DateTime expiry = DateTime.UtcNow.AddDays(10);
                    string encodedToken = "";   
                    using (var encoder = new System.Security.Cryptography.HMACSHA512(Encoding.UTF8.GetBytes(key)))   
                    {   
                        var dataToSign = id + "\n" + expiry.ToString("O");   
                        var hash = encoder.ComputeHash(Encoding.UTF8.GetBytes(dataToSign));   
                        var signature = Convert.ToBase64String(hash);   
                        encodedToken = string.Format("SharedAccessSignature uid={0}&ex={1:o}&sn={2}", id, expiry, signature);   
                    }                   
                    return encodedToken;
        }

@{
                    String subscriptionId  = (string) context.Variables["subscriptionId"];
                    string url = "{{eapim-platform-api-sandbox-baseurl}}/subscriptionkeys?environment=production&subscriptionId=" + subscriptionId;
                    return url;
                }

@{
        String subscriptionId =  context.Request.OriginalUrl.Query.GetValueOrDefault("subscriptionId");
        string url = "{{eapim-platform-management-api-baseurl}}/subscriptions/" +subscriptionId + "/listSecrets?api-version=2019-12-01";
        return url;
        }

return-response.set-body:
@{
  		        var subsDataToRead = ((IResponse)context.Variables["keysData"]).Body.As<JObject>();
            	JObject keyDataToReturn = new JObject();
            	string sPrimaryKey = subsDataToRead["primaryKey"].ToString();
                string sSecondaryKey = subsDataToRead["secondaryKey"].ToString();

                keyDataToReturn.Add("primaryKey", sPrimaryKey);
                keyDataToReturn.Add("secondaryKey", sSecondaryKey);
                return keyDataToReturn.ToString();
            }
