set-variable.value:
@{
            JObject inBody = context.Request.Body.As<JObject>(preserveContent: true); 
            string userName = inBody.GetValue("userName").ToString();
            return userName;
        }

@{
            JObject inBody = context.Request.Body.As<JObject>(preserveContent: true); 
            string userID = inBody.GetValue("userID").ToString();
            return userID;
        }

@{
            JObject inBody = context.Request.Body.As<JObject>(preserveContent: true); 
            string userEmail = inBody.GetValue("userEmail").ToString();
            return userEmail;
        }

@{
            JObject inBody = context.Request.Body.As<JObject>(preserveContent: true); 
            string organization = inBody.GetValue("applicationId").ToString();
            return organization;
        }

@{
            JObject inBody = context.Request.Body.As<JObject>(preserveContent: true); 
            string role = inBody.GetValue("description").ToString();
            return role;
        }

@{
            JObject inBody = context.Request.Body.As<JObject>(preserveContent: true); 
            JObject webObj = (JObject)inBody.GetValue("web");
            return webObj;
        }

@{
            String appId = (string) context.Variables["applicationId"];
            String backendurl = "{{eapim-platform-api-b2c-graph-applications-url}}/" + appId;
            return backendurl;
        }

send-request.set-body:
@{
                JObject web = (JObject)context.Variables["web"];
                String sdescription = (string) context.Variables["description"];
                JObject mainObj = new JObject();
                mainObj.Add("web", web);
                mainObj.Add("description", sdescription);
                return mainObj.ToString();
           }
